mapper.select:
SELECT *
FROM accessToken
WHERE accessTokenID = #{accessTokenID}
AND isExpired=0;

mapper.update:
UPDATE accessToken
SET isExpired=#{isExpired}
WHERE accessTokenID = #{accessTokenID};

UPDATE accessToken
SET confirmRequestID=#{confirmRequestID}
WHERE accessTokenID = #{accessTokenID};

mapper.delete:
DELETE
FROM accessToken
WHERE snsLoginID = #{snsLoginID}
AND uuid = #{uuid}
AND deviceType = #{deviceType};

UPDATE accessToken
SET isExpired=1
WHERE isExpired = 0
AND 
(
snsLoginID = #{snsLoginID}
OR (uuid = #{uuid}
	AND deviceType = #{deviceType}
	)
);
